	private static final String QUESTIONS_TABLE_NAME = "question";	
	private static final String RESULTS_TABLE_NAME = "result";	
	private static final String PLAYERS_TABLE_NAME = "player";
	private static final String SETTINGS_TABLE_NAME = "settings";

	Database(Context context) {
		super(context, DATABASE_NAME, null, DATABASE_VERSION);
	}

	@Override
	public void onCreate(SQLiteDatabase db) {
		db.execSQL("CREATE TABLE " + QUESTIONS_TABLE_NAME + " ("
				+ "id INTEGER PRIMARY KEY, language TEXT, content TEXT, a TEXT, b TEXT, c TEXT, answer INT);");

		db.execSQL("CREATE TABLE " + RESULTS_TABLE_NAME + " ("
				+ "id INTEGER PRIMARY KEY, player_id INTEGER, game_type INTEGER, game_language TEXT, points INTEGER, date TEXT);");

		db.execSQL("CREATE TABLE " + PLAYERS_TABLE_NAME + " ("
				+ "id INTEGER PRIMARY KEY, name TEXT, date TEXT);");

		db.execSQL("CREATE TABLE " + SETTINGS_TABLE_NAME + " ("
				+ "id INTEGER PRIMARY KEY, language TEXT);");

		db.execSQL("INSERT INTO settings(language) VALUES('en')");

		insertQuestionData(db);
	}
	
